<script>
import {TweenMax, Power2, TimelineLite} from "gsap/TweenMax";

export default {
    props:['nextLevel'],
    methods : {
        gotoNextLevel() {
            this.$router.push('/' + this.nextLevel)
        }
    },
    mounted() {
        var button = this.$refs.button;
        var tl = new TimelineLite({onComplete:loop});

        function loop() {
            tl.gotoAndPlay("spin");
        }

        tl.addLabel("spin", 1.5);

        tl.from(button, 1.5, {opacity: 0, scale: 0, ease:Bounce.easeOut})
          .to(button, 3, {rotation: 5, ease:Elastic.easeOut})
          .to(button, 3, {rotation: 0, ease:Elastic.easeOut});
    } 
}
</script>


